import React from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';

import { auth } from './firebase';
import { signOut } from 'firebase/auth';

import AdUploadScreen from './UploadAd';
function HomeScreen() {

  const route = useRoute();
  const { userData, uid } = route.params;
  const navigation = useNavigation();

  const goToClass1 = () => {
     navigation.navigate('Class1' , {uid});
  };

  const goToAdUpload = () => {
      navigation.navigate('myAdUpload' , { uid });
     console.log("Your User Id is: ",uid);
  };

  const handleSeeAds = () => {
    navigation.navigate('Ads', {uid});
  };

  const handleClasses = () => {
   navigation.navigate('Classes', {uid});
 };

 const handleUploadAudio = () => {
  navigation.navigate('myAudioUpload', {uid});
  console.log("Your User Id is: ",uid);
};

const handleListenAudio = () => {
  navigation.navigate('mySeeAudio', {uid});
  console.log("Your User Id is: ",uid);
};

  const handleSignOut = () => {
    signOut(auth)
      .then(() => {
        console.log('Signed out successfully');
        // Navigate to the desired screen upon successful sign out
        navigation.navigate('Login');
      })
      .catch((error) => {
        console.log('Error signing out:', error);
        // Handle the error if signing out fails
      });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Home</Text>
      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.button} onPress={goToClass1}>
          <Text style={styles.buttonText}>Class 1</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button} onPress={goToAdUpload}>
          <Text style={styles.buttonText}>Class 2</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button} onPress={handleSignOut}>
          <Text style={styles.buttonText}>Class 3</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button} onPress={handleSeeAds}>
          <Text style={styles.buttonText}>Class 4</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button} onPress={handleClasses}>
          <Text style={styles.buttonText}>See Class</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button} onPress={handleUploadAudio}>
          <Text style={styles.buttonText}>Class 6</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button} onPress={handleListenAudio}>
          <Text style={styles.buttonText}>Class 7</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#FFFFFF',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 30,
  },
  buttonContainer: {
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#4CAF50',
    borderRadius: 4,
    paddingVertical: 12,
    paddingHorizontal: 45,
    marginBottom: 10,
    width: 200,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttonText: {
    color: '#FFFFFF',
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});


export default HomeScreen;
