import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View, FlatList, Image } from 'react-native';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { db } from './firebase';
import { auth } from './firebase';

const AdsScreen = () => {
  const [myAds, setMyAds] = useState([]);
  const [otherAds, setOtherAds] = useState([]);

  useEffect(() => {
    // Fetch ads uploaded by yourself
    const fetchMyAds = async () => {
      try {
        const userId = auth.currentUser?.uid; // Replace with your actual user ID
        const myAdsQuery = query(collection(db, 'ads'), where('userId', '==', userId));
        const myAdsSnapshot = await getDocs(myAdsQuery);
        const myAdsData = myAdsSnapshot.docs.map((doc) => doc.data());
        setMyAds(myAdsData);
      } catch (error) {
        console.error('Error fetching my ads:', error);
      }
    };

    // Fetch ads uploaded by other people
    const fetchOtherAds = async () => {
      try {
        const userId = auth.currentUser?.uid; // Replace with your actual user ID
        const otherAdsQuery = query(collection(db, 'ads'), where('userId', '!=', userId));
        const otherAdsSnapshot = await getDocs(otherAdsQuery);
        const otherAdsData = otherAdsSnapshot.docs.map((doc) => doc.data());
        setOtherAds(otherAdsData);
      } catch (error) {
        console.error('Error fetching other ads:', error);
      }
    };

    fetchMyAds();
    fetchOtherAds();
  }, []);

  const renderAdItem = ({ item }) => (
    <View style={styles.adItem}>
    <Image source={{ uri: item.imageUrl }} style={styles.adImage} />
      <Text style={styles.adTitle}>{item.title}</Text>
      <Text style={styles.adDescription}>{item.description}</Text>
    </View>
  );

  const renderMyAdItem = ({ item }) => (
    <View style={styles.myAdItem}>
    <Image source={{ uri: item.imageUrl }} style={styles.adImage} />
      <Text style={styles.adTitle}>{item.title}</Text>
      <Text style={styles.adDescription}>{item.description}</Text>
    </View>
  );
  
  const renderOtherAdItem = ({ item }) => (
    <View style={styles.otherAdItem}>
    <Image source={{ uri: item.imageUrl }} style={styles.adImage} />
      <Text style={styles.adTitle}>{item.title}</Text>
      <Text style={styles.adDescription}>{item.description}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>My Ads</Text>
      <FlatList
        data={myAds}
        renderItem={renderMyAdItem}
        keyExtractor={(item) => item.adId}
        style={styles.adList}
      />

      <Text style={styles.title}>Other Ads</Text>
      <FlatList
        data={otherAds}
        renderItem={renderOtherAdItem}
        keyExtractor={(item) => item.adId}
        style={styles.adList}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007AFF',
    marginTop: 20,
    marginLeft: 20,
  },
  adList: {
    marginTop: 10,
    paddingHorizontal: 20,
  },
  adItem: {
    borderWidth: 1,
    borderColor: '#D3D3D3',
    borderRadius: 4,
    padding: 10,
    marginBottom: 10,
  },
  myAdItem: {
    borderWidth: 1,
    borderColor: '#D3D3D3',
    borderRadius: 4,
    padding: 10,
    marginBottom: 10,
  },
  otherAdItem: {
    borderWidth: 1,
    borderColor: '#D3D3D3',
    borderRadius: 4,
    padding: 10,
    marginBottom: 10,
  },
  adTitle: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  adDescription: {
    marginTop: 5,
    color: '#888888',
  },
  adImage: {
    width: 100,
    height: 100,
    marginBottom: 10,
  },
});

export default AdsScreen;
